CREATE TABLE IF NOT EXISTS `tb_bookinfo` (
  `id` int(10) unsigned NOT NULL COMMENT '编号',
  `bookName` varchar(70) NOT NULL COMMENT '书名',
  `author` varchar(30) NOT NULL COMMENT '作者',
  `typeId` int(10) unsigned NOT NULL COMMENT '类型',
  `publishing` varchar(30) NOT NULL COMMENT '出版社',
  `ISBN` varchar(20) NOT NULL COMMENT 'ISBN',
  `click` int(11) NOT NULL COMMENT '点击量',
  `price` float(8,2) DEFAULT NULL COMMENT '价格',
  `page` int(10) DEFAULT NULL COMMENT '页数',
  `format` varchar(10) DEFAULT NULL COMMENT '开本',
  `packing` varchar(10) DEFAULT NULL COMMENT '包装',
  `image` text COMMENT '图片',
  `imageBig` text COMMENT '大图片',
  `titleBrief` mediumtext COMMENT '标题简介',
  `contentBrief` text COMMENT '内容简介',
  `authorBrief` text COMMENT '作者简介'
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8 COMMENT='图书信息表';

